  `solar_system_name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `overview` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `when_discovered` text COLLATE utf8mb4_unicode_ci NOT NULL,
